import React, { useEffect, useState } from "react";
import RoleItem from "../../components/RoleItem/RoleItem";
import { URL_GOOGLE_SHEETS } from "../../constants";

const RolezandoList = () => {
  const [list, setList] = useState();
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    const data = fetch(URL_GOOGLE_SHEETS).then((res) => res.text());

    data.then((result) => console.log(result));
  }, []);

  const convertData = () => {
    let csv = `"Carimbo de data/hora",
      "Endereço de e-mail",
      "Nome do lugar?",
      "Cidade:",
      "Categoria: ",
      "Preço:",
      "Tem espaço para crianças?",
      "Nota para o lugar:",
      "Comentários:";
    "08/10/2023 19:18:42",
      "keyla.klc@gmail.com",
      "Teste",
      "Sorocaba",
      "Pizzaria, Hamburgueria, Churrascaria, Japonesa",
      "4",
      "Não",
      "2",
      "Sem notas"`;

    const array = csv.split(",");

    let result = [];

    let headers = array[0].split("; ");

    for (let i = 1; i < array.length - 1; i++) {
      let obj = {};

      let str = array[i];
      let s = "";

      let flag = 0;
      for (let ch of str) {
        if (ch === '"' && flag === 0) {
          flag = 1;
        } else if (ch === '"' && flag == 1) flag = 0;
        if (ch === ", " && flag === 0) ch = "|";
        if (ch !== '"') s += ch;
      }

      let properties = s.split("|");

      for (let j in headers) {
        if (properties[j].includes(", ")) {
          obj[headers[j]] = properties[j]
            .split(", ")
            .map((item) => item.trim());
        } else obj[headers[j]] = properties[j];
      }

      result.push(obj);
    }

    let json = result;

    console.log(json);
  };

  convertData();

  return (
    <>
      {/* {list.map((item) => (
        <RoleItem data={item} />
      ))} */}
    </>
  );
};

export default RolezandoList;
