import React, { useEffect, useState } from "react";
import RoleItem from "../../components/RoleItem/RoleItem";
import { URL_GOOGLE_SHEETS } from "../../constants";
import {
  Accordion,
  AccordionItem,
  AccordionItemHeading,
  AccordionItemButton,
  AccordionItemPanel,
} from "react-accessible-accordion";

const RolezandoList = () => {
  const [list, setList] = useState([]);

  useEffect(() => {
    const data = fetch(URL_GOOGLE_SHEETS).then((res) => res.text());

    data.then((result) => {
      const resp = result
        .replace("google.visualization.Query.setResponse(", "")
        .replace("/*O_o*/", "")
        .replace("\n", "")
        .replace(");", "");

      const response = JSON.parse(resp);

      let data = [];

      const headers = response.table.cols.map(({ label }) => label);

      response.table.rows.forEach((row) => {
        let value = {};

        row.c.forEach(({ v }, index) => {
          value = { ...value, [headers[index]]: v };
        });

        data.push({ ...value });
      });

      setList(data);
    });
  }, []);

  const newRole = () => {
    window.open(`https://forms.gle/1QL1qnvKjEHUPUwaA`, "_blank");
  };

  return (
    <>
      <Accordion>
        {list.map(({ item, index }) => {
          return (
            <AccordionItem>
              <AccordionItemHeading>
                <AccordionItemButton>{item.name}</AccordionItemButton>
              </AccordionItemHeading>
              <AccordionItemPanel>
                <p>
                  Exercitation in fugiat est ut ad ea cupidatat ut in cupidatat
                  occaecat ut occaecat consequat est minim minim esse tempor
                  laborum consequat esse adipisicing eu reprehenderit enim.
                </p>
              </AccordionItemPanel>
            </AccordionItem>
          );
        })}
      </Accordion>
    </>
  );
};

export default RolezandoList;
