import React, { useEffect, useState } from "react";
import RoleItem from "../../components/RoleItem/RoleItem";
import { URL_GOOGLE_SHEETS } from "../../constants";
import {
  Accordion,
  AccordionItem,
  AccordionItemHeading,
  AccordionItemButton,
  AccordionItemPanel,
} from "react-accessible-accordion";
import GradeCounter from "../../components/GradeCounter/GradeCounter";
import CashCounter from "../../components/CashCounter/CashCounter";

const RolezandoList = () => {
  const [list, setList] = useState([]);

  useEffect(() => {
    const data = fetch(URL_GOOGLE_SHEETS).then((res) => res.text());

    data.then((result) => {
      const resp = result
        .replace("google.visualization.Query.setResponse(", "")
        .replace("/*O_o*/", "")
        .replace("\n", "")
        .replace(");", "");

      const response = JSON.parse(resp);

      let data = [];

      const headers = response.table.cols.map(({ label }) => label);

      response.table.rows.forEach((row) => {
        let value = {};

        row.c.forEach(({ v }, index) => {
          value = { ...value, [headers[index]]: v };
        });

        data.push({ ...value });
      });

      setList(data);
    });
  }, []);

  const newRole = () => {
    window.open(`https://forms.gle/1QL1qnvKjEHUPUwaA`, "_blank");
  };

  const mapsRedirect = (nome) => {
    window.open(`https://www.google.com.br/maps/search/${nome}`, "_blank");
  };

  return (
    <>
      <Accordion>
        {list.map((item, index) => {
          return (
            <AccordionItem style={{ margin: "15px" }}>
              <AccordionItemHeading>
                <AccordionItemButton>
                  <div
                    style={{
                      height: "50px",
                      borderRadius: "5px 5px 0px 0px",
                      backgroundColor: "white",
                      display: "flex",
                      justifyContent: "space-between",
                      padding: "5px 15px",
                    }}
                  >
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "center",
                        flexDirection: "column",
                      }}
                    >
                      {item.nome}
                    </div>
                    <GradeCounter counter={item.nota} />
                  </div>
                </AccordionItemButton>
              </AccordionItemHeading>
              <AccordionItemPanel>
                <div
                  style={{
                    borderRadius: "0px 0px 5px 5px",
                    minHeight: "100px",
                    backgroundColor: "white",
                    padding: "20px 15px",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      justifyContent: "space-between",
                      textAlign: "initial",
                      gap: "10px",
                    }}
                  >
                    <div>
                      <div>{item.categoria}</div>
                      <div>{`Possui espaço kids? ${item.kids}`}</div>
                    </div>
                    <div>
                      <div>{item.cidade}</div>
                    </div>
                  </div>
                  <div
                    style={{
                      textAlign: "initial",
                    }}
                  >
                    <CashCounter counter={item.valor} />
                    <div>{`Comentário: ${
                      item.comentario ?? "Sem comentário"
                    }`}</div>
                  </div>
                  {/* <div> */}
                  <button
                    style={{
                      width: "100%",
                      marginTop: "20px",
                      backgroundColor: "#4564fb",
                      border: "none",
                      height: "30px",
                      color: "white",
                      fontSize: "18px",
                    }}
                    onClick={() => mapsRedirect(item.nome)}
                  >
                    Let's bora
                  </button>
                  {/* </div> */}
                </div>
              </AccordionItemPanel>
            </AccordionItem>
          );
        })}
      </Accordion>
    </>
  );
};

export default RolezandoList;
