import React, { useEffect, useState } from "react";
import RoleItem from "../../components/RoleItem/RoleItem";
import { URL_GOOGLE_SHEETS } from "../../constants";

const RolezandoList = () => {
  const [list, setList] = useState([]);
  const [hasError, setHasError] = useState(false);

  useEffect(() => {
    const data = fetch(URL_GOOGLE_SHEETS).then((res) => res.text());

    data.then((result) => setList(result));
  }, []);

  const convertData = (data) => {
    var jsonData = [];
    var headers = [];

    data.then((result) => {
      var rows = list.split("\r\n");

      for (var i = 0; i < rows.length; i++) {
        var cells = rows[i].split(",");
        var rowData = {};
        for (var j = 0; j < cells.length; j++) {
          if (i == 0) {
            var headerName = cells[j].trim();
            headers.push(headerName);
          } else {
            var key = headers[j];
            if (key) {
              rowData[key] = cells[j].trim();
            }
          }
        }
        //skip the first row (header) data
        if (i != 0) {
          jsonData.push(rowData);
        }
      }

      console.log(jsonData);
    });
  };

  return (
    <>
      {list.map((item) => (
        <RoleItem data={item} />
      ))}
    </>
  );
};

export default RolezandoList;
