import React from "react";
import { Label } from "../Label/Label";
import {
  ContainerAssessment,
  ContainerCard,
  ContainerHeader,
  ContainerIcon,
  ContainerInformations,
} from "./components";
import GradeCounter from "../GradeCounter/GradeCounter";
import CashCounter from "../CashCounter/CashCounter";

const RoleItem = ({ item }) => {
  const mapsRedirect = () => {
    window.open(`https://www.google.com.br/maps/search/${item.nome}`, "_blank");
  };

  return (
    <ContainerCard>
      <ContainerIcon>Icon</ContainerIcon>
      <ContainerInformations>
        <ContainerHeader>
          <Label style={{ fontWeight: "bold", fontSize: "30px" }}>
            {item.nome}
          </Label>
          <button onClick={mapsRedirect}>Maps</button>
        </ContainerHeader>
        <Label>{item.categoria}</Label>
        <Label>{item.cidade}</Label>
        {item.kids === "Sim" && <Label>Possui espa√ßo kids</Label>}
        <ContainerAssessment>
          <GradeCounter counter={item.nota} />
          <CashCounter counter={item.valor} />
        </ContainerAssessment>
      </ContainerInformations>
    </ContainerCard>
  );
};

export default RoleItem;
